import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import formatDate from '../../utils/formatDate';
import { connect } from 'react-redux';
import { deleteAllergy } from '../../actions/allergy';

const AllergyItem = ({
  deleteAllergy,
  auth,
  allergy: { _id, allergyName , note, date },
  showActions
}) => (
  <div>
    <div>
    <h2 class="my-2">Immunization Records on {formatDate(date)}</h2>
      <table class="table">
        <thead>
          <tr>
            <th>Vaccine Name</th>
            <th>Note</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{allergyName}</td>
            <td>{note}</td>
          </tr>
        </tbody>
      </table>

      {showActions && (
        <Fragment>
            <button
              onClick={() => deletePost(_id)}
              type="button"
              className="btn btn-danger"
            >
              <i className="fas fa-times" />
            </button>
        </Fragment>
      )}
    </div>
  </div>
);

AllergyItem.defaultProps = {
  showActions: true
};

AllergyItem.propTypes = {
  allergy: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  deleteAllergy: PropTypes.func.isRequired,
  showActions: PropTypes.bool
};

const mapStateToProps = (state) => ({
  auth: state.auth
});

export default connect(mapStateToProps, { deleteAllergy })(
  AllergyItem
);
