import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import formatDate from '../../utils/formatDate';
import { connect } from 'react-redux';
import { deletePost } from '../../actions/post';

const PostItem = ({

  deletePost,
  auth,
  post: { _id, vaccineName, user, date },
  showActions
}) => (
  <div className="post bg-white p-1 my-1">
    <div>
      <Link to={`/profile/${user}`}>
      </Link>
    </div>
    <div>

    <h2 class="my-2">Immunization Records</h2>
      <table class="table">
        <thead>
          <tr>
            <th class="hide-sm">Vaccine Name</th>
            <th class="hide-sm">Vaccine Manufacturer Name</th>
            <th class="hide-sm">Expiry Date</th>
            <th class="hide-sm">Batch/Lot Number</th>
            <th class="hide-sm">Dose Quantity</th>
            <th class="hide-sm">Target Disease</th>
            <th class="hide-sm">Performer's Name</th>
            <th class="hide-sm">Vaccine Name</th>

            <th></th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Immunized on {formatDate(date)}</td>
            <td class="hide-sm">{vaccineName}</td>
            <td>
              <button class="btn btn-danger">
                Delete
              </button>
            </td>
          </tr>
        </tbody>
      </table>

      {showActions && (
        <Fragment>
          {!auth.loading && user === auth.user._id && (
            <button
              onClick={() => deletePost(_id)}
              type="button"
              className="btn btn-danger"
            >
              <i className="fas fa-times" />
            </button>
          )}
        </Fragment>
      )}
    </div>
  </div>
);

PostItem.defaultProps = {
  showActions: true
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  addLike: PropTypes.func.isRequired,
  removeLike: PropTypes.func.isRequired,
  deletePost: PropTypes.func.isRequired,
  showActions: PropTypes.bool
};

const mapStateToProps = (state) => ({
  auth: state.auth
});

export default connect(mapStateToProps, { addLike, removeLike, deletePost })(
  PostItem
);
