import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addPost } from '../../actions/post';

const PostForm = ({ addPost }) => {
  const [vaccineName, setVaccineName] = useState('');
  const [vaccineManufacturerName, setVaccineManufacturerName] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [lotNumber, setLotNumber] = useState('');
  const [doseQty, setDoseQty] = useState('');
  const [targetDiesease, setTargetDiesease] = useState('');
  const [performerName, setPerformerName] = useState('');
  const [institutionName, setInstitutionName] = useState('');
  const [institutionType, setInstitutionType] = useState('');
  const [location, setLocation] = useState('');
  const [note, setNote] = useState('');

  return (
    <div className='post-form'>
      <form
        className='form my-1'
        onSubmit={e => {
          e.preventDefault();
          addPost({ vaccineName,vaccineManufacturerName,expiryDate,lotNumber,doseQty,targetDiesease,performerName,institutionName ,institutionType,location,note});
          setVaccineName('');
          setLotNumber('');
        }}
      >
        <textarea
          name='text'
          cols='30'
          rows='5'
          placeholder='Create a post'
          value={vaccineName}
          onChange={e => setVaccineName(e.target.value)}
          required
        />

        <textarea
          name='lotNumber'
          placeholder='Batch/Lot Number'
          value={lotNumber}
          onChange={e => setLotNumber(e.target.value)}
          required
        />
        <input type='submit' className='btn btn-dark my-1' value='Submit' />
      </form>
    </div>
  );
};

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired
};

export default connect(
  null,
  { addPost }
)(PostForm);
