import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import formatDate from '../../utils/formatDate';
import { connect } from 'react-redux';
import { deleteImmunize} from '../../actions/immunize'
// import { deletePost } from '../../actions/post';

const ImmunizeItem = ({
  deleteImmunize,
  auth,
  immunize: { _id, user,vaccineName, date },
  showActions
}) => (
  <div className="post bg-white p-1 my-1">
    
    <div>
      <p className="my-1">{vaccineName}</p>
      <p className="post-date">Posted on {formatDate(date)}</p>

      {showActions && (
        <Fragment>
          {!auth.loading && user === auth.user._id && (
            <button
              onClick={() => deleteImmunize(_id)}
              type="button"
              className="btn btn-danger"
            >
              <i className="fas fa-times" />
            </button>
          )}
        </Fragment>
      )}
    </div>
  </div>
);

ImmunizeItem.defaultProps = {
  showActions: true
};

ImmunizeItem.propTypes = {
  immunize: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  deleteImmunize: PropTypes.func.isRequired,
  showActions: PropTypes.bool
};

const mapStateToProps = (state) => ({
  auth: state.auth
});

export default connect(mapStateToProps, { deleteImmunize })(
  ImmunizeItem
);
