import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { deleteAllergy } from '../../actions/allergy';

const AllergyItem = ({
  allergy: {
    allergyName,
    note,
    date,
  }
}) => {
  return (
    <div >
    <h2 className="my-2">Allergy Recorded on {date}</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Allergy Name</th>
            <th>Note</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{allergyName}</td>
            <td>{note}</td>
          </tr>
        </tbody>
      </table>
    </div>  
      
  );
};

AllergyItem.propTypes = {
  allergy: PropTypes.array.isRequired
};

export default AllergyItem;
