import { deleteAllergy } from '../../actions/allergy';

const AllergyItem = ({
  deleteAllergy,
  auth,
  // allergy: { _id, allergyName, user,note, date },
  showActions
}) => (
  <div> 
    {/* <div>
    <h2 class="my-2">Allergies Recorded on {formatDate(date)}</h2>
