import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import formatDate from '../../utils/formatDate';
import { connect } from 'react-redux';
import { deletePost } from '../../actions/post';

const PostItem = ({
  deletePost,
  auth,
  post: { _id, vaccineName, vaccineManufacturerName,expiryDate,lotNumber,doseQty,targetDiesease,performerName,
    institutionName,institutionType,location,note, user, date },
  showActions
}) => (
  <div>
    <div>
    <h2 class="my-2">Immunization Records on {formatDate(date)}</h2>
      <table class="table">
        <thead>
          <tr>
            <th class="hide-sm">Vaccine Name</th>
            <th class="hide-sm">Vaccine Manufacturer Name</th>
            <th class="hide-sm">Expiry Date</th>
            <th class="hide-sm">Batch/Lot Number</th>
            <th class="hide-sm">Dose Quantity</th>
            <th class="hide-sm">Target Disease</th>
            <th class="hide-sm">Performer's Name</th>
            <th class="hide-sm">Institution Name</th>
            <th class="hide-sm">Institution Type</th>
            <th class="hide-sm">Note</th>

            <th></th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{vaccineName}</td>
            <td>{vaccineManufacturerName}</td>
            <td>{formatDate(date)}</td>
            <td>{lotNumber}</td>
            <td>{doseQty}</td>
            <td>{targetDiesease}</td>
            <td>{performerName}</td>
            <td>{institutionName}</td>
            <td>{institutionType}</td>
            <td>{note}</td>
          </tr>
        </tbody>
      </table>

      {showActions && (
        <Fragment>
          <button
            onClick={() => addLike(_id)}
            type="button"
            className="btn btn-light"
          >
            <i className="fas fa-thumbs-up" />{' '}
            <span>{likes.length > 0 && <span>{likes.length}</span>}</span>
          </button>
          <button
            onClick={() => removeLike(_id)}
            type="button"
            className="btn btn-light"
          >
            <i className="fas fa-thumbs-down" />
          </button>
          <Link to={`/posts/${_id}`} className="btn btn-primary">
            Discussion{' '}
            {comments.length > 0 && (
              <span className="comment-count">{comments.length}</span>
            )}
          </Link>
          {!auth.loading && user === auth.user._id && (
            <button
              onClick={() => deletePost(_id)}
              type="button"
              className="btn btn-danger"
            >
              <i className="fas fa-times" />
            </button>
          )}
        </Fragment>
      )}
    </div>
  </div>
);

PostItem.defaultProps = {
  showActions: true
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  deletePost: PropTypes.func.isRequired,
  showActions: PropTypes.bool
};

const mapStateToProps = (state) => ({
  auth: state.auth
});

export default connect(mapStateToProps, { deletePost })(
  PostItem
);
